package com.sigmatrix.sc.web.vo;

import java.io.Serializable;

/** 
 * @author  
 * @date 创建时间：2016年7月15日 下午3:20:08 
 * @parameter 4.18查询权限接口  
 * @return 
 */
public class Permission implements Serializable {
	//权限ID
	private String id;
	public Permission() {
		super();
		// TODO Auto-generated constructor stub
	}
	public Permission(String id, String name, String permission,
			String description, String status, String createTime,
			String createId, String updateTime, String updateId) {
		super();
		this.id = id;
		this.name = name;
		this.permission = permission;
		this.description = description;
		this.status = status;
		this.createTime = createTime;
		this.createId = createId;
		this.updateTime = updateTime;
		this.updateId = updateId;
	}
	public String getId() {
		return id;
	}
	public void setId(String id) {
		this.id = id;
	}
	public String getName() {
		return name;
	}
	public void setName(String name) {
		this.name = name;
	}
	public String getPermission() {
		return permission;
	}
	public void setPermission(String permission) {
		this.permission = permission;
	}
	public String getDescription() {
		return description;
	}
	public void setDescription(String description) {
		this.description = description;
	}
	public String getStatus() {
		return status;
	}
	public void setStatus(String status) {
		this.status = status;
	}
	public String getCreateTime() {
		return createTime;
	}
	public void setCreateTime(String createTime) {
		this.createTime = createTime;
	}
	public String getCreateId() {
		return createId;
	}
	public void setCreateId(String createId) {
		this.createId = createId;
	}
	public String getUpdateTime() {
		return updateTime;
	}
	public void setUpdateTime(String updateTime) {
		this.updateTime = updateTime;
	}
	public String getUpdateId() {
		return updateId;
	}
	public void setUpdateId(String updateId) {
		this.updateId = updateId;
	}
	//权限名称
	private String name;
	//权限标识
	private String permission;
	//权限描述
	private String description;
	//状态：-1删除；0停用；1正常
	private String status;
	//创建时间
	private String createTime;
	//创建者用户主键ID
	private String createId;
	//最后修改时间
	private String updateTime;
	//最后修改者用户主键ID
	private String updateId;
}
